export const isForgeElement = (auxNode) => {
    return (auxNode !== null && typeof auxNode !== 'boolean' && auxNode !== undefined);
};
export const isJiraContext = (context) => context.type === 'jira';
export const isContextMenuExtensionContext = (extensionContext) => extensionContext.type === 'contextMenu';
export const isContentActionExtensionContext = (extensionContext) => extensionContext.type === 'contentAction';
